SELECT o.animal_id,o.animal_type,o.name from animal_ins i join animal_outs o on i.animal_id=o.animal_id where o.animal_id in (select animal_id from animal_ins where sex_upon_intake like '%Intact%') and (sex_upon_outcome like '%Spayed%' or sex_upon_outcome like '%Neutered%') order by i.animal_id;